    private void reg_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_reg_btnActionPerformed
            Model_fees fee = new Model_fees();
            D_fees d_fee = new D_fees();

            // Retrieve values from text fields
            String student_names = student_field.getText();
            String reg_number = reg_number_field.getText();
            String department = depart_field.getText();
            String academic_year_str = academic_field.getText();
            String programme = programme_field.getText();
            String trim_fee_str = trimester_field.getText();
            String sup_fee_str = supervision_field.getText();
            String reg_fee_str = reg_field.getText();
            String grad_fee_str = grad_field.getText();

            // Validation
            if (student_names.isEmpty() || reg_number.isEmpty() || department.isEmpty() || academic_year_str.isEmpty() || programme.isEmpty() || trim_fee_str.isEmpty() || sup_fee_str.isEmpty() || reg_fee_str.isEmpty() || grad_fee_str.isEmpty()) {
                JOptionPane.showMessageDialog(null, "Please fill all the requested fields");
                return; // halt the execution 
            }

            // Convert academic year, fees to numeric values
            int academic_year = Integer.parseInt(academic_year_str);
            double trim_fee = Double.parseDouble(trim_fee_str);
            double sup_fee = Double.parseDouble(sup_fee_str);
            double reg_fee = Double.parseDouble(reg_fee_str);
            double grad_fee = Double.parseDouble(grad_fee_str);

            // Additional validation for numeric values
            if (academic_year <= 0 || trim_fee <= 0 || sup_fee <= 0 || reg_fee <= 0 || grad_fee <= 0) {
                JOptionPane.showMessageDialog(null, "Invalid input for numeric fields");
                return; // halt the execution 
            }

            // Set fee information
            fee.setStudent_names(student_names);
            fee.setReg_number(reg_number);
            fee.setDepartment(department);
            fee.setAcademic_year(academic_year);
            fee.setProgramme(programme);
            fee.setTrimester_fees(trim_fee);
            fee.setSupervision_fees(sup_fee);
            fee.setRegistration_fees(reg_fee);
            fee.setGraduation_fees(grad_fee);

            // Insert fee information into the database
            d_fee.create_student(fee);

       
    }//GEN-LAST:event_reg_btnActionPerformed

    private void annual_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_annual_btnActionPerformed
        String trim_fee_str = trimester_field.getText();
        String reg_fee_str = reg_field.getText();

        // Check if any of the required fields are empty
        if (trim_fee_str.isEmpty() || reg_fee_str.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Please fill all the required fields");
            return; // halt the execution 
        }

        // Perform calculation
        double trim_fee = Double.parseDouble(trim_fee_str);
        double reg_fee = Double.parseDouble(reg_fee_str);
        double total = (trim_fee + reg_fee ) * 3;

        display_field.setText(String.valueOf(total) +" Rwf");
        
    }//GEN-LAST:event_annual_btnActionPerformed

    private void three_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_three_btnActionPerformed
        String trim_fee_str = trimester_field.getText();
        String reg_fee_str = reg_field.getText();
        String sup_fee_str = supervision_field.getText();
        String grad_fee_str = grad_field.getText();

        // Check if any of the required fields are empty
        if (trim_fee_str.isEmpty() || reg_fee_str.isEmpty() || sup_fee_str.isEmpty() || grad_fee_str.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Please fill all the required fields");
            return; // halt the execution 
        }

        // Perform calculation
        double trim_fee = Double.parseDouble(trim_fee_str);
        double reg_fee = Double.parseDouble(reg_fee_str);
        double sup_fee = Double.parseDouble(sup_fee_str);
        double grad_fee = Double.parseDouble(grad_fee_str);
        double total = (((trim_fee + reg_fee )*3)*3 )+ sup_fee + grad_fee;

        display_field.setText(String.valueOf(total)+" Rwf");
        
    }//GEN-LAST:event_three_btnActionPerformed

