    private void retrieve_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_retrieve_btnActionPerformed
      populateTable();
    }//GEN-LAST:event_retrieve_btnActionPerformed

    
    private void populateTable() {
        D_fees d_fee = new D_fees();
        DefaultTableModel model = (DefaultTableModel) data_table.getModel();

        model.setRowCount(0);

         List <Model_fees> fees = d_fee.display_students();

         for (Model_fees fee : fees){

             Object [] row = {

                 fee.getStudent_names(),
                 fee.getTrimester_fees(),
                 fee.getSupervision_fees(),
                 fee.getGraduation_fees()
             };

             model.addRow(row);
         }
}
